import React from 'react';

class Facts extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            counter: 0,
        };
    }

    componentDidMount() {
        this.timerID = setInterval(
            () => this.tick(), 1000
        );
    }

    componentWillUnmount() {
        clearInterval(this.timerID);
    }

    tick(){
        if (this.state.counter > this.props.dataFacts.length){
            this.setState({
                counter: 0,
            })
        }

        this.setState({
            counter: this.state.counter + 1,
        })
    }

    render() {
        console.log(this.props.dataFacts.length)
        const dataFacts = this.props.dataFacts;
        const factsList = dataFacts.map((value) => <li>{value}</li>);
        return (
            <div>
                {factsList[this.state.counter]}
            </div>
        )
    }
}

export default Facts;
