import React from 'react';

class Facts extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            counter: 0,
        };
    }

    componentDidMount() {
        this.timerID = setInterval(
            () => this.tick(), 1000
        );
    }

    componentWillUnmount() {
        clearInterval(this.timerID);
    }

    tick() {
        const tickState = this.state;
        const tickProps = this.props;
        this.setState(
            function(tickState, tickProps){
                if (tickState.counter <= tickProps.dataFacts.length){
                    return {
                        counter: this.state.counter + 1,
                    }
                }
            }
        );
    }

    render() {
        const dataFacts = this.props.dataFacts;
        const factsList = dataFacts.map((value) => <li>{value}</li>);
        return (
            <div>
                {factsList[this.state.counter]}
            </div>
        )
    }
}

export default Facts;
